pragma solidity 0.4.24;

import "./ScriptVerification.sol";
import "./FundManager.sol";
import "./WitnessEngine.sol";


contract Burner is FundManager {

    mapping (bytes32 => uint) private amountOfSats;
    mapping (bytes32 => bytes) private redeemScripts;
    mapping (bytes32 => bool) private isUsed;
    mapping (bytes32 => bytes32) private txIds;
    mapping (bytes32 => bytes20) private rsHashes;
    mapping (bytes32 => bytes32) private secretHashes;
    mapping (bytes32 => address) private orderToAddress;
    mapping (bytes32 => address) private processers;
    mapping (bytes32 => uint) private amounts;
    mapping (address => mapping(address => uint256)) private tokenLockedBalances;

    ScriptVerification private sv;

    WitnessEngine private we;

    EIP20Interface private btct;

    event Submitted(bytes32 _reqHash, bytes _redeemScript);
    event ConfirmedByProcessor(bytes32 _reqHash, bytes32 _txId);

    constructor(address _sv, address _we, address _btct) public { 
        sv = ScriptVerification(_sv);
        we = WitnessEngine(_we);
        btct = EIP20Interface(_btct);
    }

    function submitReq(uint _amountOfSat, bytes _redeemScript) public returns(bool) {

        bytes32 reqHash = sha256(abi.encodePacked(_amountOfSat, _redeemScript));
        bytes20 rsHash;
        bytes32 secretHash;

        require(!isUsed[reqHash]);

        amountOfSats[reqHash] = _amountOfSat;
        redeemScripts[reqHash] = _redeemScript;
        isUsed[reqHash] = true;
        orderToAddress[reqHash] = msg.sender;

        (rsHash, secretHash) = sv.redeemScriptToSecretHash(_redeemScript);

        rsHashes[reqHash] = rsHash;
        amounts[reqHash] = msg.value;
        secretHashes[reqHash] = secretHash;

        emit Submitted(reqHash, _redeemScript);

    }

    function confirmByProcessor(bytes32 _reqHash, bytes32 _txId) public {

        require(txIds[_reqHash] == 0x0);

        require(isUsed[_reqHash]);

        txIds[_reqHash] = _txId;

        processers[_reqHash] = msg.sender;

        emit ConfirmedByProcessor(_reqHash, _txId);

    }

    /**
     *  bytes _rawTx, 
        bytes32 _txId, 
        bytes20 _beneficially, 
        uint _amount, 
        uint _fee
     */
    function confirmByWitness(bytes32 _reqHash, bytes _rawTx) public {

        require(txIds[_reqHash] != 0x0);

        require(isUsed[_reqHash]);

        require(we.isWitness(msg.sender));

        uint amountOfSat = amountOfSats[_reqHash];

        bytes32 txId = txIds[_reqHash];

        bytes20 beneficially = rsHashes[_reqHash];

        require(sv.verifyTx(_rawTx, txId, beneficially, amountOfSat, 0));

    }

    function execute(bytes32 _reqHash, bytes _secret) public {

        require(secretHashes[_reqHash] == sha256(_secret));

        address user = orderToAddress[_reqHash];

        uint amountOfSat = amountOfSats[_reqHash];

        burnBTCT(user, amountOfSat);

        unlockSecurityDeposit(btct, user);
    }

    function burnBTCT(address _user, uint _amountOfSat) internal {

        require(balanceOfToken(btct, _user) >= _amountOfSat);

        tokenBalances[btct][_user] -= _amountOfSat;   

        //btct.burn(_amountOfSat);
    }

    function unlockSecurityDeposit(address token, address _user) internal {

        uint lockedAmount = tokenLockedBalances[token][_user];

        tokenBalances[token][_user] += lockedAmount;
    }
}
